  `author` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `author_info` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `publish` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `price` decimal(10, 2) NOT NULL,
  `summary` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `status` int(1) NOT NULL,
  `sort_id` int(3) NOT NULL,
  `cover_url` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `isbn` char(13) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `pubdate` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `pages` int(5) NOT NULL,
  `binding` varchar(25) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `average` varchar(5) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `numRaters` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `sort_id`(`sort_id`) USING BTREE,
  INDEX `book_id`(`book_id`) USING BTREE,
  CONSTRAINT `book_ibfk_1` FOREIGN KEY (`sort_id`) REFERENCES `sort` (`sort_id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 127 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

